ection="no" type="grid" angled_section="no" text_align="left" background_image_as_pattern="without_pattern" background_color="#323232" padding_top="87" padding_bottom="2" css_animation=""][vc_column][vc_column_text]\r\n<h2 style="text-align: center;"><span style="color: #ffffff;">THE SIR AUSTIN MOVING PROCESS</span></h2>\r\n[/vc_column_text][vc_separator type="small" position="center" color="#ffffff" up="7" down="37"][vc_column_text]\r\n<h4 style="text-align: center;"><span style="color: #ffffff;">Sir Austin has devised the perfect process to almost guarantee a perfect move</span></h4>\r\n[/vc_column_text][vc_separator type="transparent" up="55" down="2"][vc_row_inner row_type="row" type="full_width" text_align="left" css_animation="" padding="10"][vc_column_inner width="1/2"][icon_text box_type="normal" icon="fa-camera-retro" icon_type="circle" icon_position="left" icon_size="fa-3x" use_custom_icon_size="no" title="PLANNING YOUR MOVE" separator="no" text="The perfect move starts out with the perfect plan. Planning your move from start to finish guarantees that you will have a roadmap for how you want your move to go." icon_color="#ffffff" title_color="#ffffff" text_color="#ffffff"][vc_separator type="transparent" up="40" down="0"][/vc_column_inner][vc_column_inner width="1/2"][icon_text box_type="normal" icon="fa-hdd-o" icon_type="circle" icon_position="left" icon_size="fa-3x" use_custom_icon_size="no" title="PROTECTING YOUR BELONGINGS" separator="no" text="Sir Austin is all about protection and he goes to great lengths to ensure that his customers possessions are protected too. Don\'t trust your prized possessions to anyone else." icon_color="#ffffff" title_color="#ffffff" text_color="#ffffff"][vc_separator type="transparent" up="40" down="0"][/vc_column_inner][/vc_row_inner][vc_row_inner row_type="row" type="full_width" text_align="left" css_animation="" padding="10"][vc_column_inner width="1/2"][icon_text box_type="normal" icon="fa-bell-o" icon_type="circle" ico